import { useState } from 'react';
import DarkMode from './components/DarkMode';
import {RxCross1} from "react-icons/rx"
import {AiFillHeart} from "react-icons/ai"
import { Switch } from '@headlessui/react';

function TodoList({darkMode,setDarkMode}) {
  const [value, setValue] = useState('');
  const [todos, setTodos] = useState([]);


  function handleSubmit(event) {
    event.preventDefault();
    addTodo();
    setValue('');
  }


  function addTodo() {
    const newTodo = value;
    setTodos([...todos, newTodo]);
    console.log([...todos, newTodo]);
  }

  function removeTodo(index) {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  }

  return (
    <div className='w-[80%] h-[100%] p-4 md:w-[60%] lg:w-[50%] flex flex-col justify-center m-auto shadow-lg rounded-lg'>
      <h1 className='my-6 text-center text-3xl font-bold tracking-tight text-gray-900'>Todo List</h1>
      {/* button */}

      <div className={darkMode ? 'dark' : ''+"px-2"}>
      <Switch
        checked={darkMode}
        onChange={setDarkMode}
        className={`${darkMode ? 'bg-gray-800' : 'bg-gray-200'}
          relative inline-flex items-center h-6 rounded-full w-11`}
      >
        <span className="sr-only">Enable dark mode</span>
        <span
          className={`${darkMode ? 'translate-x-6 transition-all' : 'translate-x-1 transition-all'}
            inline-block w-4 h-4 transform bg-white rounded-full`}
        />
      </Switch>
      <p className={darkMode?`text-pink-400`:`text-blue-500`}>This text will be styled differently in dark mode.</p>
    </div>

      {/* form */}
      <form onSubmit={handleSubmit} className="flex justify-center gap-2 p-4">
      <input
        placeholder='Enter your TODO'
        type="text"
        value={value}
        onChange={(event) => setValue(event.target.value)}
        className="block w-full px-2 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
      />
      <button type="submit" className='group relative justify-center rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600'>Add</button>
    </form>

      <ul className='w-full flex flex-col justify-center gap-4 my-4 p-4'>
        {todos.map((todo, index) => (
          <li key={index}
              className="flex w-full m-auto px-2 justify-between rounded-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          >

            <p className='flex justify-center align-center text-center'><AiFillHeart className='text-indigo-500 m-auto mr-2'/>{todo}</p>
            <button 
            onClick={() => removeTodo(index)}
            className="text-indigo-500"
            >
                <RxCross1 />
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
