import { useState } from 'react';
import DarkMode from './DarkMode';

function TodoList({DarkMode}) {
  const [value, setValue] = useState('');
  const [todos, setTodos] = useState([]);


  function handleSubmit(event) {
    event.preventDefault();
    addTodo();
    setValue('');
  }


  function addTodo() {
    const newTodo = value;
    setTodos([...todos, newTodo]);
    console.log([...todos, newTodo]);
  }

  function removeTodo(index) {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  }

  return (
    <div>
      <h1 className='text-center'>Todo List</h1>
      <form onSubmit={handleSubmit} className="flex justify-center">
      <input
        type="text"
        value={value}
        onChange={(event) => setValue(event.target.value)}
        className="ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
      />
      <button type="submit" className='group relative justify-center rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600'>Add</button>
    </form>

      <ul className='flex flex-col justify-center'>
        {todos.map((todo, index) => (
          <li key={index}
              className="rounded-b-md border-0 py-1.5 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:z-10 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
          >
            {todo}
            <button 
            onClick={() => removeTodo(index)}
            className="group relative justify-center rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
                Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
