import { useState, useEffect } from 'react';
import { Switch } from '@headlessui/react';

function DarkMode() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    setDarkMode(prefersDarkMode);
  }, []);

  return (
    <div className={darkMode ? 'dark' : ''}>
      <Switch
        checked={darkMode}
        onChange={setDarkMode}
        className={`${darkMode ? 'bg-gray-800' : 'bg-gray-200'}
          relative inline-flex items-center h-6 rounded-full w-11`}
      >
        <span className="sr-only">Enable dark mode</span>
        <span
          className={`${darkMode ? 'translate-x-6 transition' : 'translate-x-1 transition-all'}
            inline-block w-4 h-4 transform bg-white rounded-full`}
        />
      </Switch>
      <p className="text-gray-800 dark:text-gray-200">This text will be styled differently in dark mode.</p>
    </div>
  );
}

export default DarkMode;
